void XuLiNhietDo(){
  if(distance > maximumRange){
    objectTemp = 0;
    HienThiNhietDo(objectTemp);
    HienThiCanhBaoXa();
    delay(1000);
  }
  if((distance >= minimumRange) && (distance <= maximumRange)){
    // đo 5 lần lấy trung bình
    if(readCount == 10){
        objectTemp = temp / 10;
        HienThiNhietDo(objectTemp);
        readCount = 0;
        temp = 0;
        Blynk.virtualWrite(V0, objectTemp);
        if(objectTemp <= 30){
          Serial.println(objectTemp);
          HienThiNhietDoThap();
          delay(1500);
        }else if(objectTemp >= 37.5){
          // tft hiện thị nhiệt độ, cảnh báo
          // gửi lệnh chụp ảnh đến esp32 cam qua uart
          SerialPort.println("captures");
          SerialPort.println(objectTemp);
          Blynk.logEvent("Warning");
          HienThiSot();
          Coi();
          delay(1500);
        }else{
          // tft hiện thị nhiệt độ, an toàn
          //SerialPort.println("captures");
          SerialPort.println(objectTemp);
          HienThiAnToan();
          delay(1500);
        }
    }else{
      // tft hiện thị vui lòng giữ yên 5s
      HienThiLoading();
      temp = temp + objectTemp;
      readCount++;
      delayTime = 150;
    }
  }else{
    // nếu khoảng cách sai
    temp = 0;
    delayTime = 100;
    readCount = 0;
  }
}

void setup() {
  Serial.begin(115200);
  SerialPort.begin(115200, SERIAL_8N1, 16, 17); 
  mlx.begin();
  Blynk.begin(auth, ssid, password);
  pinMode(trigPin, OUTPUT); // Sets the trigPin as an Output
  pinMode(echoPin, INPUT);  // Sets the echoPin as an Input
  pinMode(buzzer, OUTPUT);
  tft.initR(INITR_BLACKTAB);
  tft.fillScreen(ST7735_BLACK); //----------------------------------------------cài đặt màu nền
  printText("Nhiet Do", CYAN,15,80,2);
  printText("o", WHITE, 95, 115, 1);
  printText("C", WHITE, 100, 120, 2);
  tft.drawBitmap(5, 105, NhietDo, 20, 40, WHITE);
  initWifi();
}

void loop() {
  Blynk.run();
  XuLiKhoangCach();
  objectTemp = measurementError + mlx.readObjectTempC() ;  
  roomTemp = mlx.readAmbientTempC() ;  
  
  // Serial
  Serial.println("Nhiet do doi tuong:" + String(objectTemp) + ", Nhiet do phong:" + String(roomTemp));
  Serial.println("Khoang cach: " + String(distance));

  XuLiNhietDo();
  delay(delayTime);
}